mapper.select:
SELECT DISTINCT INPUT_NUMBER as "inputNumber", INPUT_NAME as "inputName", INPUT_QUANTITY as "inputQuantity", COST as "cost", TO_CHAR(EXPIRE_DATE,'yyyy/mm/dd') as "expireDate", PRODUCT_ID as "productId", SUPPLIER_ID as "supplierId", TO_CHAR(INPUT_DATE,'yyyy/mm/dd') as "inputDate", (SELECT SUPPLIER_NAME FROM SUPPLIER WHERE SUPPLIER.SUPPLIER_ID = INPUT.SUPPLIER_ID)AS "supplierName" FROM INPUT
INPUT.PRODUCT_ID = #{productId}
AND INPUT.SUPPLIER_ID = #{supplierId}
ORDER BY INPUT_DATE DESC

mapper.delete:
DELETE FROM INPUT
WHERE INPUT_NUMBER IN

#{item}
