mapper.select:
SELECT INPUT_NUMBER as "inputNumber", INPUT_NAME as "inputName", INPUT_QUANTITY as "inputQuantity", COST as "cost", EXPIRE_DATE as "expireDate", PRODUCT_ID as "productId", SUPPLIER_ID as "supplierId", INPUT_DATE as "inputDate" FROM INPUT
INPUT.PRODUCT_ID = #{productId}
INPUT.SUPPLIER_ID = #{supplierId}
ORDER BY INPUT_DATE DESC

mapper.delete:
DELETE FROM INPUT
WHERE INPUT_NUMBER IN

#{item}
