mapper.select:
SELECT A.*, B.EMPLOYEE_NAME FROM WORK_MANAGE A , EMP_MANAGE B WHERE A.EMPLOYEE_ID=B.EMPLOYEE_ID
AND A.EMPLOYEE_ID=#{employeeId}
AND SUBSTR(to_char(A.WORK_START,'hh24:mi'),1,2)=SUBSTR(#{workStart},1,2)
AND SUBSTR(to_char(A.WORK_END,'hh24:mi'),1,2)=SUBSTR(#{workEnd},1,2)
AND A.DAY_WORKTIME=#{dayWorktime}
ORDER BY A.WORK_START DESC

mapper.insert:

INSERT INTO WORK_MANAGE( EMPLOYEE_ID,
						 WORK_START
						)
VALUES (#{employeeId},
		SYSDATE
	   )

mapper.update:
UPDATE WORK_MANAGE
SET	WORK_END=SYSDATE
WHERE EMPLOYEE_ID = #{employeeId}
